import React from 'react';
import { useState, useEffect } from "react"; 

import {TouchableOpacity, ListItem,StyleSheet,Image, Button, View, SafeAreaView, Text,TextInput, Alert,ScrollView } from 'react-native';
import SwitchSelector from 'react-native-switch-selector';
import Contacto from '../components/contacto';
import ProductoF from '../components/productoF';
import Axios from "axios";
import ServerData from '../serverData';
import { FlatList } from 'react-native-gesture-handler';


const SwitchOp = [
    {label:'contactos',value:'Contactos'},
    {label:'productos',value:'Productos'},
];





const Favoritos = ({navigation}) =>{
    const [tex, setTex] = useState('')
    var Value = tex;
    const [filteredData, setfilteredData] = useState([]);
    const [contactos, setcontactos] = useState([]);
    const [productos, setproductos] = useState([]);
    const [search,setsearch] = useState('');
    var prods;

    useEffect(()=>{
        fetchProductos();
        fetchContactos();


        return()=>{

        }
    },[])



    const fetchContactos =() =>{
        const api = ServerData+"/getFav/"+"89960823";
       //console.log(api)
        fetch(api)
        .then((response)=>response.json())
        .then((responseJson) =>{

            //console.log(responseJson);
            setfilteredData(responseJson);
            setcontactos(responseJson);
        }).catch((error) =>{
            console.log(error);
        });   
    }



    const fetchProductos =() =>{
        const datos = require('../local_data/productosFav.json');
        prods = datos;

       setproductos(datos);
    }

    const searchFilter = (text) =>{
        if(text){
            const newData = contactos.filter((item)=>{
                const itemData = item.nombre ? item.nombre.toUpperCase()
                :''.toUpperCase();
                const textData = text.toUpperCase();
                return itemData.indexOf(textData)>-1;
            });
            setfilteredData(newData);
            setsearch(text);
        }else{
            setfilteredData(contactos);
            setsearch(text);
        }
    }

    const ContactView =({item})=>{
        return(
            <Contacto  nombre={item.nombre} numero = {item.telefono}/>
        )
    }
    const ProductView =({item})=>{


       //console.log(item);

        return(
            <ProductoF ruta={item.img} tipo={item.pez} vendedor = {item.nombre}/>
        )

    }




  return(
<SafeAreaView style = {styles.container} >

    <Text style = {styles.title}  >
        Favoritos
    </Text>
    <View >
    <View style = {styles.contenedorSw}>
        <View style = {styles.row}>

                <TextInput
                style = {styles.search}
                value={search}
                placeholder="   Buscar"
                onChangeText = {(text)=>searchFilter(text)}
                />
                <Image  style = {styles.ima} source={require('../assets/img/search-icon.png')} />
        </View>
    <SwitchSelector
    textColor={'#838383'}
    buttonColor={"#EE7333"}
    selectedColor={"#FFFFFF"}
    backgroundColor={"rgba(131, 131, 131, 0.12)"}
    height={43}

    options={SwitchOp}
    initial={0}
    onPress={(Value) => {setTex(Value);  }}
    />
    </View>
    </View>
    <View style = {styles.contenedorLista}>

        <SafeAreaView>
            {
                 (tex=="Productos") &&

                 <FlatList 
                   nestedScrollEnabled
                   data={productos}
                   keyExtractor={(item,index)=>index.toString()}
                   renderItem={ProductView}
                   />  



            }
            {
                (tex!="Productos") &&
                <FlatList 
                   nestedScrollEnabled
                   data={filteredData}
                   keyExtractor={(item,index)=>index.toString()}
                   renderItem={ContactView}
                   />  

            }


        </SafeAreaView>




    </View>



</SafeAreaView>



  );
};
const styles = StyleSheet.create({
    contenedorSw:{
       width:"100%",
   },
   search:{
        marginLeft:20,
        marginBottom:22,
        marginTop:17,
        height:40,
        width:"90%",
        backgroundColor:"white",
        borderRadius:14,


   },
   ima:{
    width:35,
    height:35,
    marginRight:20,
    marginTop:20,
    },
    row:{
        flexDirection:"row",

    },
   contenedorLista:{

    width:"100%",
    margin:"auto",
    marginTop:10,
    marginBottom:30,
    justifyContent: 'center',
    },
    title:{
        marginTop:"5%",
        textAlign:'center',
        fontSize: 16    ,
    fontWeight: "bold",
    },
    container: {

      padding:30,
      backgroundColor: '#FFFFFF',
      flex: 1,    
      justifyContent: 'flex-start',
      alignContent:'center'

    },
    boton:{
        backgroundColor: '#FFFFFF',
    },


});

export default Favoritos;
